DROP DATABASE IF EXISTS mortgage_analysis;
CREATE DATABASE mortgage_analysis;
DROP TABLE IF EXISTS acq;
CREATE TABLE acq (id SERIAL PRIMARY KEY, loan_id VARCHAR(255), orig_channel VARCHAR(255), seller_name VARCHAR(255), orig_interest_rate VARCHAR(255), orig_upb VARCHAR(255), orig_loan_term VARCHAR(255), orig_date VARCHAR(255), first_pay_date VARCHAR(255), orig_ltv VARCHAR(255), orig_cltv VARCHAR(255), num_borrowers VARCHAR(255), dti VARCHAR(255), borrower_credit_score VARCHAR(255), first_home_buyer VARCHAR(255), loan_purpose VARCHAR(255), property_type VARCHAR(255), num_units VARCHAR(255), occupancy_status VARCHAR(255), property_state VARCHAR(255), zip VARCHAR(255), mortgage_insurance_percent VARCHAR(255), product_type VARCHAR(255), coborrow_credit_score VARCHAR(255), mortgage_insurance_type VARCHAR(255), relocation_mortgage_indicator VARCHAR(255), dummy VARCHAR(255));
DROP TABLE IF EXISTS perf;
CREATE TABLE perf (id SERIAL PRIMARY KEY, loan_id VARCHAR(255), monthly_reporting_period VARCHAR(255), servicer VARCHAR(255), interest_rate VARCHAR(255), current_actual_upb VARCHAR(255), loan_age VARCHAR(255), remaining_months_to_legal_maturity VARCHAR(255), adj_remaining_months_to_maturity VARCHAR(255), maturity_date VARCHAR(255), msa VARCHAR(255), current_loan_delinquency_status VARCHAR(255), mod_flag VARCHAR(255), zero_balance_code VARCHAR(255), zero_balance_effective_date VARCHAR(255), last_paid_installment_date VARCHAR(255), foreclosed_after VARCHAR(255), disposition_date VARCHAR(255), foreclosure_costs VARCHAR(255), prop_preservation_and_reair_costs VARCHAR(255), asset_recovery_costs VARCHAR(255), misc_holding_expenses VARCHAR(255), holding_taxes VARCHAR(255), net_sale_proceeds VARCHAR(255), credit_enhancement_proceeds VARCHAR(255), repurchase_make_whole_proceeds VARCHAR(255), other_foreclosure_proceeds VARCHAR(255), non_interest_bearing_upb VARCHAR(255), principal_forgiveness_upb VARCHAR(255), repurchase_make_whole_proceeds_flag VARCHAR(255), foreclosure_principal_write_off_amount VARCHAR(255), servicing_activity_indicator VARCHAR(255));
